import NavBar from '@/components/NavBar';
import { getProfile } from '@/store/actions/profile';
import { List, DatePicker, Space, Toast } from 'antd-mobile';
import { Drawer } from 'antd-mobile-v2'
import { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './index.module.scss';
import { useDispatch, useSelector } from 'react-redux';
import classNames from 'classnames';
import EditInput
 from './components/EditInput';


export default function Edit() {
    const navigate = useNavigate();
    const [visible, setVisible] = useState(false);
    const dispatch = useDispatch();
    const userInfo = useSelector(state => state.profile.profile);
    console.log(userInfo);

    useEffect(() => {
        dispatch(getProfile());
    }, []);

    const fileRef = useRef(null)

  // 控制全屏抽屉的显示与隐藏
  const [open, setOpen] = useState({
    visible: false,
    type: ''
  })

  // 控制列表抽屉的显示与隐藏
  const [listOpen, setListOpen] = useState({
    visible: false,
    type: ''
  })

  const onClose = () => {
    setOpen({
      visible: false,
      type: ''
    })
    setListOpen({
      visible: false,
      type: ''
    })
  }

//   const onFileChange = async e => {
//     //  获取文件
//     const file = e.target.files[0]
//     // console.log(e)
//     const fd = new FormData()

//     // 上传文件
//     fd.append('photo', file)
//     await dispatch(updatePhoto(fd))

//     Toast.success('图片上传成功')
//     onClose()
//   }

//   const onBirthdayChange = e => {
//     // console.log(e)
//     onCommit('birthday', dayjs(e).format('YYYY-MM-DD'))
//   }



    return (
        <div className={styles.root}>
            <div className='content'>
                {/* 顶部导航栏 */}
                <NavBar onExtraClick={() => navigate(-1)}>个人信息</NavBar>
                {/* 中间内容区域 */}
                <div className='wrapper'>
                    {/* 列表1、显示头像、昵称、简介 */}
                    <List className='profile-list'>
                        <List.Item extra={
                            <span className='avatar-wrapper'>
                                <img src={userInfo.photo} alt="" />
                            </span>
                        }
                            onClick={() => { }}
                        >头像</List.Item>
                        <List.Item extra={
                            <input className='name-input' type='text' value={userInfo.mobile} />
                        } onClick={() => { }}>昵称</List.Item>
                        <List.Item extra={
                            <span className={classNames('intro', userInfo.intro ? 'normal' : '')}>
                                <input className='name-input' type='text' value={userInfo.intro || '未填写'} />
                            </span>
                        } onClick={() => { }}>简介</List.Item>
                    </List>
                    <Space />
                    {/* 列表2、显示性别、生日 */}
                    <List className='profile-list'>
                        <List.Item extra={userInfo.gender === 1 ? '男' : '女'} onClick={() => { }}>性别</List.Item>
                        <DatePicker
                            value={new Date(userInfo.birthday)}
                            visible={visible}
                            onClose={() => { setVisible(false) }}
                            defaultValue={new Date()}
                            max={new Date()}
                            min={new Date('1900-01-01')}
                            title='时间选择'
                        >
                            {value =>
                                <List.Item
                                    extra={!!userInfo.birthday ? userInfo.birthday : value.getFullYear() + '-' + (value.getMonth() + 1) + '-' + value.getDate()}
                                    onClick={() => { setVisible(true) }}
                                >生日
                                </List.Item>
                            }
                        </DatePicker>

                    </List>
                </div>

            </div>
            {/* 底部栏：退出登录按钮 */}
            <div className='logout'>
                <button className='btn'>退出登录</button>
            </div>

            {/* 全屏抽屉组件 */}
            <Drawer
                sidebar={open.visible && <EditInput onClose={onClose} type={open.type} onCommit={onCommit} />}
                open={open.visible}
                className="drawer"
                position="right"
            >
                {''}
            </Drawer>

            {/* 列表抽屉组件 */}
            {/* 头像、性别 */}
            <Drawer
                className="drawer-list"
                position="bottom"
                sidebar={listOpen.visible && <EditList config={config} onClose={onClose} type={listOpen.type}></EditList>}
                open={listOpen.visible}
                onOpenChange={onClose}
            >
                {''}
            </Drawer>
        </div>
    )
}
