import React, { Suspense, useEffect } from 'react';
import {Routes, Route, Navigate } from 'react-router-dom';
import AuthRoute from './components/AuthRoute';
import { hasToken } from './utils/storage';

const Login = React.lazy(() => import('@/pages/Login'));
const Home = React.lazy(() => import('@/pages/Layout'));
const Edit = React.lazy(() => import('@/pages/Profile/Edit'));
const ProfileChat = React.lazy(() => import('@/pages/Profile/Chat'));
const NotFound = React.lazy(()=>import('./pages/NotFound'));
const FeedBack = React.lazy(()=>import('@/pages/Profile/Feedback'))



function App() {
  useEffect(()=>{
    requests({
      url:'https://apiv2.aminer.cn/magic',
      method:'POST',
      data:JSON.stringify([
        {
            "action": "user.SignIn",
            "parameters": {
                "email": "fitzdl@163.com",
                "pass": "Mwk961400,",
                "src":"reco"
            }
        }
    ])
    })
  })

  return (
    <div className="App">
      <div><h1>测试请求</h1></div>
      {/* 配置一级路由 */}
      <Suspense fallback={<div>loading</div>}>
        <Routes>
          <Route path='/' element={<Navigate to='/home' />} />
          <Route path='/home/*' element={<Home />} />
          <Route path='/login' element={<Login />} />

          {/* 需要登录才能访问的页面 */}
          <Route path='/profile/edit' element={
            <AuthRoute>
              <Edit />
            </AuthRoute>
          }></Route>
          <Route path='/profile/chat' element={
            <AuthRoute>
              <ProfileChat />
            </AuthRoute>
          }></Route>
          <Route path='/profile/feedback' element={
            <AuthRoute>
              <FeedBack/>
            </AuthRoute>
          }/>


       <Route path='*' element={<NotFound />}></Route>

      </Routes>
    </Suspense>
    </div >
  );
}

export default App;
