import styles from './index.module.scss';
import requests from '@/utils/request';
import ArticleItem from '../ArticleItem';
import { useEffect, useState } from 'react';
/**
 * 文章列表组件
 * @param {String} props.channelId 当前文章列表所对应的频道ID
 * @param {String} props.aid 当前 Tab 栏选中的频道ID,高亮的pin'dao
 */
const ArticleList = ({ channelId, aid }) => {
    const [list, setList] = useState([])
    useEffect(() => {
        const load = async () => {
            const res = await requests.get('/articles', {
                params: {
                    channel_id: channelId,
                    timestamp: Date.now(),
                },
            })
            setList(res.data.results);
        }
        if (channelId === aid) {
            load();
        }
    }, [channelId, aid]);
    return (
        <div className={styles.root}>
            <div className="articles">
                {list.map((item) => (
                    <div className="article-item" key={item.art_id}>
                        <ArticleItem className="article-item" article={item}></ArticleItem>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default ArticleList
