import NavBar from '@/components/NavBar'
import Input from '@/components/Input'
import styles from './index.module.scss'
import {useFormik} from 'formik'

export default function Login() {
  const onExtraClick = ()=>{
    console.log("send code!!!");
  }
  const validate = values => {
    const errors = {};
    if (!values.mobile) {
      errors.mobile = '手机号不能为空!';
    } else if (values.mobile.length != 11) {
      errors.mobile = '手机号必须为11位!';
    }

    if (!values.code) {
      errors.code = '验证码不能为空!';
    } else if (values.code.length != 6) {
      errors.code = '请输入6位有效验证码!';
    }

    return errors;
  };
  const formik = useFormik({
    initialValues:{
      mobile:'',
      code:''
    },
    onSubmit:values=>{
      console.log(values);
    },
    validate,
  });
  const {values:{mobile,code}, handleChange,handleSubmit} = formik;
  console.log(formik);
  return (
    <div className={styles.root}>
      <NavBar>登录</NavBar>

      {/* 内容 */}
      <div className='content'>
        <h3>短信登录</h3>
        <form onSubmit={handleSubmit}>
          <div className='input-item'>
            <Input 
              placeholder='请输入手机号' 
              autoComplete='off'
              value={mobile}
              onChange={handleChange}
              name='mobile'
            />
            {formik.errors.mobile?<div className='validate'>{formik.errors.mobile}</div>:<div className='validate'>手机号验证错误信息</div>}

            {/* <div className='validate'>手机号验证错误信息</div> */}
          </div>
          <div className='input-item'>
            <Input 
              placeholder='请输入验证码' 
              autoComplete='off'
              extra='发送验证码' 
              onExtraClick={onExtraClick}
              value={code}
              onChange={handleChange}
              name='code'
            />
            {formik.errors.code?<div className='validate'>{formik.errors.code}</div>:null}
          </div>
          <button type='submit' className='login-btn'>登录</button>
        </form>

      </div>
    </div>
  )
}
