import styles from './index.module.scss';
import ArticleItem from '../ArticleItem';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getArticleList } from '@/store/actions/home';
import { PullToRefresh } from 'antd-mobile';
/**
 * 文章列表组件
 * @param {String} props.channelId 当前文章列表所对应的频道ID
 * @param {String} props.aid 当前 Tab 栏选中的频道ID,高亮的频道id
 */
const ArticleList = ({ channelId, aid }) => {
    const res = useSelector((state) => state.home.articles[channelId])
    const dispatch = useDispatch();
    const onRefresh = () => {
        // console.log(1111);
        dispatch(getArticleList(channelId, Date.now()))
    }
    // 列表滚动到底部自动加载更多数据。
    const [hasMore, setHasMore] = useState(true)
    async function loadMore() {
        const append = await mockRequest()
        setData(val => [...val, ...Array(append.length).fill(item)])
        setHasMore(append.length > 0)
    }
    useEffect(() => {
        // 如果redux中 有数据  不用发送请求
        if (res) return;
        if (channelId === aid) {
            dispatch(getArticleList(channelId, Date.now()))
        }
    }, [channelId, aid, dispatch])
    const { list } = res || []
    if (!res) return null


    return (
        <div className={styles.root}>
            <div className="articles">
                <PullToRefresh
                    onRefresh={onRefresh}
                >
                    {list.map((item) => (
                        <div className="article-item" key={item.art_id}>
                            <ArticleItem article={item}></ArticleItem>
                        </div>
                    ))}
                </PullToRefresh>
            </div>
        </div>
    )
}

export default ArticleList
