// 存放有关login的action
import request from '@/utils/request'

// 发送短信验证码
export const sendValidationCode = mobile=>{
    return async (dispatch)=>{
        const res = await request.get(`/sms/codes/${mobile}`);
        console.log(res);
    }
}


export const login = (data)=>{
    return (dispatch)=>{
        request()
    }
}
