import NavBar from '@/components/NavBar';
import { List, DatePicker, Space } from 'antd-mobile';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './index.module.scss'

export default function Edit() {
    const navigate  = useNavigate();
    const [visible, setVisible] = useState(false);

    return (
        <div className={styles.root}>
            <div className='content'>
                {/* 顶部导航栏 */}
                <NavBar onExtraClick={()=>navigate(-1)}>个人信息</NavBar>

                {/* 列表1、显示头像、昵称、简介 */}
                <List className='profile-list'>
                    <List.Item extra={
                            <span className='avatar-wrapper'>
                                <img src={''} alt="" />
                            </span>
                        } 
                        onClick={() => { }}
                    >头像</List.Item>
                    <List.Item extra={'13323551400'} onClick={() => { }}>昵称</List.Item>
                    <List.Item extra={
                        <span className='intro'>{'未填写'}</span>
                    } onClick={() => { }}>简介</List.Item>
                </List>
                <Space />
                {/* 列表2、显示性别、生日 */}
                <List className='profile-list'>
                    <List.Item extra={'女'} onClick={() => { }}>性别</List.Item>
                    <DatePicker
                        visible={visible}
                        onClose={() => { setVisible(false) }}
                        defaultValue={new Date()}
                        max={new Date()}
                        min={new Date('1900-01-01')}
                        title='时间选择'
                    >
                        {value =>
                            <List.Item
                                extra={value.getFullYear() + '-' + (value.getMonth() + 1) + '-' + value.getDate()}
                                onClick={() => { setVisible(true) }}
                            >生日
                            </List.Item>
                        }
                    </DatePicker>

                </List>
            </div>



        </div>
    )
}
