import { SAVE_USER } from "../constant";

const initValues = {
    user:{},
    profile:{},
};

export default function reducer(state = initValues, action){
    const {type, payload} = action;
    if(type === SAVE_USER){
        return{
            ...state,
            user:payload,
        }
    }
    return state
}
