import classnames from 'classnames'
import Icon from '@/components/Icon'
import styles from './index.module.scss'
import dayjs from 'dayjs'
import relativeTime from 'dayjs/plugin/relativeTime'
import 'dayjs/locale/zh-cn'
import Img from '@/components/Img'
import { useDispatch, useSelector } from 'react-redux'
import { setMoreAction } from '@/store/action/home'

dayjs.extend(relativeTime)
const ArticleItem = ({ article, channelId }) => {
  const {
    cover: { images, type },
    title,
    aut_name,
    comm_count,
    pubdate
  } = article

  const dispatch = useDispatch()
  const isLoading = useSelector(state => !!state.login.token)

  return (
    <div className={styles.root}>
      <div className={classnames('article-content', { t3: type === 3, 'none-mt': type === 0 })}>
        <h3>{title}</h3>
        {type !== 0 && (
          <div className="article-imgs">
            {images.map((item, i) => (
              <div className="article-img-wrapper" key={i}>
                <Img src={item} alt="" />
              </div>
            ))}
          </div>
        )}
      </div>
      <div className={classnames('article-info', type === 0 ? 'none-mt' : '')}>
        <span>{aut_name}</span>
        <span>{comm_count} 评论</span>
        <span>{dayjs(pubdate).locale('zh-cn').fromNow()}</span>

        <span className="close">
          {isLoading && (
            <Icon
              type="iconbtn_essay_close"
              onClick={() => {
                dispatch(
                  setMoreAction({
                    visible: true,
                    articleId: article.art_id,
                    channelId
                  })
                )
              }}
            />
          )}
        </span>
      </div>
    </div>
  )
}

export default ArticleItem
