import React from 'react';
import styles from './index.module.scss';
import Tabs from '@/components/Tabs';
import Icon from '@/components/Icon';
import Channels from './components/Channels';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllChannels, getUserChannels } from '@/store/actions/home';
import { Drawer } from 'antd-mobile-v2';



export default function Home() {
  // 处理频道高亮
  const [active, setActive] = useState(0)
  const changeActive = (index) => {
    setActive(index)
  }

  // 控制抽屉组件
  // 控制频道管理抽屉的显示和隐藏
  const [drawerVisible, setDrawerVisible] = useState(false)
  const onClose = () => {
    setDrawerVisible(false)
  }
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getUserChannels());
  }, [])
  useEffect(() => {
    dispatch(getAllChannels());
  }, [])
  const tabs = useSelector((state) => state.home.userChannels);
  // const tabs = [
  //   {
  //     id: 1,
  //     name: '频道1',
  //   },
  //   {
  //     id: 2,
  //     name: '频道2',
  //   },
  //   {
  //     id: 3,
  //     name: '频道3',
  //   },
  //   {
  //     id: 4,
  //     name: '频道4',
  //   },
  //   {
  //     id: 5,
  //     name: '频道5',
  //   },
  // ]
  return (
    <div className={styles.root}>
      <Tabs tabs={tabs} index={active} onChange></Tabs>

      {/* 频道 Tab 栏右侧的两个图标按钮：搜索、频道管理 */}
      <div className="tabs-opration">
        <Icon type="icon-search" />
        <Icon type="icon-more" onClick={() => setDrawerVisible(true)} />
      </div>
      {/* 频道管理抽屉 */}
      <Drawer
        className="my-drawer"
        children={''}
        sidebar={drawerVisible && <Channels onClose={onClose} />}
        open={drawerVisible}
      />
    </div>
  )
}
