import { List, DatePicker } from 'antd-mobile';
import { useState } from 'react';

export default function Edit() {
    const [visible, setVisible] = useState(false);
    const [dateValue, setDateValue] = useState();
    console.log(dateValue);
    return (
        <div>
            <NavBar>个人信息</NavBar>

            <List>
                <List.Item extra={'图片'} onClick={() => { }}>头像</List.Item>
                <List.Item extra={'13323551400'} onClick={() => { }}>昵称</List.Item>
                <List.Item extra={'未填写'} onClick={() => { }}>简介</List.Item>
            </List>
            <List>
                <List.Item extra={'女'} onClick={() => { }}>性别</List.Item>
                <List.Item extra={'123124'}  onClick={() => { setVisible(true) }}>生日
                    <DatePicker
                        visible={visible}
                        onClose={() => { setVisible(false) }}
                        defaultValue={new Date()}
                        max={new Date()}
                    >
                        {/* <List.Item onClick={() => {}}>生日</List.Item> */}

                        {value => value?.toDateString()}
                    </DatePicker>
                </List.Item>

            </List>

        </div>
    )
}
