import NavBar from '@/components/NavBar';
import { getProfile } from '@/store/actions/profile';
import { List, DatePicker, Space,} from 'antd-mobile';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './index.module.scss';
import { useDispatch, useSelector } from 'react-redux';
import classNames from 'classnames';


export default function Edit() {
    const navigate = useNavigate();
    const [visible, setVisible] = useState(false);
    const dispatch = useDispatch();
    const userInfo = useSelector(state => state.profile.profile);
    console.log(userInfo);
    useEffect(() => {
        dispatch(getProfile());
    }, []);
    console.log(!!userInfo.brithday);
    console.log(!!userInfo.birthday);


    return (
        <div className={styles.root}>
            <div className='content'>
                {/* 顶部导航栏 */}
                <NavBar onExtraClick={() => navigate(-1)}>个人信息</NavBar>
                {/* 中间内容区域 */}
                <div className='wrapper'>
                    {/* 列表1、显示头像、昵称、简介 */}
                    <List className='profile-list'>
                        <List.Item extra={
                            <span className='avatar-wrapper'>
                                <img src={userInfo.photo} alt="" />
                            </span>
                        }
                            onClick={() => { }}
                        >头像</List.Item>
                        <List.Item extra={
                            <input className='name_' type='text' value={userInfo.mobile}/>
                        } onClick={() => { }}>昵称</List.Item>
                        <List.Item extra={
                            <span className={classNames('intro', userInfo.intro ? 'normal' : '')}>{userInfo.intro || '未填写'}</span>
                        } onClick={() => { }}>简介</List.Item>
                    </List>
                    <Space />
                    {/* 列表2、显示性别、生日 */}
                    <List className='profile-list'>
                        <List.Item extra={userInfo.gender===1?'男':'女'} onClick={() => { }}>性别</List.Item>
                        <DatePicker
                            value={new Date(userInfo.birthday)}
                            visible={visible}
                            onClose={() => { setVisible(false) }}
                            defaultValue={new Date()}
                            max={new Date()}
                            min={new Date('1900-01-01')}
                            title='时间选择'
                        >
                            {value =>
                                <List.Item
                                    extra={!!userInfo.birthday?userInfo.birthday:value.getFullYear() + '-' + (value.getMonth() + 1) + '-' + value.getDate()}
                                    onClick={() => { setVisible(true) }}
                                >生日
                                </List.Item> 
                            }
                        </DatePicker>

                    </List>
                </div>

            </div>

            {/* 底部栏：退出登录按钮 */}
            <div className='logout'>
                <button className='btn'>退出登录</button>
            </div>
        </div>
    )
}
