// 初始状态
const initialState = {
    userChannels: [],
  }

  const home = (state = initialState, action) => {
    const { type, payload } = action

    switch (type) {
      case 'home/channel':
        return {
          ...state,
          userChannels: payload,
        }

      default:
        return state
    }
  }

  export default home
